import findCinemaItem from "./FindCinemaItem"; //从movieLi 跳转过来时，要把 city_id (vuex获取),mid(movieLi)获取过来

export default {
  data: function data() {
    return {
      mid: "",
      list: [],
      isNone: true
    };
  },
  props: {},
  computed: {
    city_id: function city_id() {
      //获取vuex保存的city_id
      return this.$store.state.city_id;
    }
  },
  methods: {
    // 获取mid的函数
    routeMid: function routeMid() {
      this.mid = this.$route.query.mid;
    },
    //ajax请求
    load: function load() {
      var _this = this;

      var url = "cinema/v1/find_cinema";
      var obj = {
        city_id: this.city_id,
        mid: this.mid
      };
      console.log(obj);
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        if (res.data.code == 0) {
          console.log(res.data.msg);
          _this.isNone = false;
        } else {
          _this.list = res.data.data;
          console.log(_this.list);
        }
      });
    }
  },
  created: function created() {
    // 在组件创建成功时，获取mid
    // console.log(this.$route.query);
    this.routeMid();
    this.load();
  },
  components: {
    findCinemaItem: findCinemaItem
  }
};
